import React from "react";
import apiconfig from '../../../config/api.config.json'
import axios from "axios";
import { Link } from "react-router-dom";
import viewmahsiswa from "./viewmahasiswa"
import { MDBDataTable } from "mdbreact";

class View {}

class DatatablePage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      //showCreateMahasiswa: false,
      mahasiswa: [],
      listmahasiswa: [],
      currentmahasiswa: {}
    };
    this.viewkodeMahasiswaHandler = this.viewKodeMahasiswaHandler.bind(this)
    this.editKodeMahasiswaHandler = this.editKodeMahasiswaHandler.bind(this)
    this.deleteKodeMahasiswaHandler = this.deleteKodeMahasiswaHandler.bind(this)
    this.kodeMahasiswaStatus = this.kodeMahasiswaStatus.bind(this)
  }

  kodeMahasiswaStatus(){
    this.setState({
        viewMahasiswa:     false,
        editMahasiswa:     false,
        deleteMahasiswa:   false

    })
}
editKodeMahasiswaHandler(kode_mahasiswa){
  this.state.mahasiswa.map((ele) =>{
      if(kode_mahasiswa == ele.kode_mahasiswa){
          this.setState({
              currentMahasiswa: ele,
              editMahasiswa: true
          })
      }
  })
}

getlistmahasiswa(){
  let token = localStorage.getItem(apiconfig.ls.token)
  let option = {
     url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.mahasiswa,
    method: "get",
    headers : {
      "Authorization" : token
    }
  }
    axios(option)
      .then(response => {
          let tmp =[]
          response.data.message.map((row,x) => {
              let c = <Link to ="#">
            <span onClick= {() => {this.viewKodeMahasiswaHandler(row.ode_mahasiswa)}} class='fa fa-search' style={{fontSize : '18px', paddingRight :'30px'}}/>
            <span onClick= {() => {this.editKodeMahasiswaHandler(row.kode_mahasiswa)}} class="fa fa-edit" style={{fontSize : '18px', paddingRight: '30px'}}/>
            <span onClick= {() => {this.deleteKodeMahasiswaHandler(row.kode_mahasiswa)}}  class="fa fa-trash" style={{fontSize : '18px'}}/>
            </Link>
            tmp.push({"no":x+1, "kode_mahasiswa":row.kode_mahasiswa, "nama_mahasiswa":row.nama_mahasiswa,
             "alamat":row.alamat, "kode_agama":row.kode_agama, "kode_jurusan":row.kode_jurusan, c})
          })
        
          this.setState({
         listMahasiswa:tmp,
            mahasiswa: response.data.message
        });
      })
      .catch(error => {
        alert(error);
      });
  }
  viewKodeMahasiswaHandler(kode_mahasiswa) {
      let tmp = {}
      this.state.mahasiswa.map((ele)=>{
          if(kode_mahasiswa == ele.kode_mahasiswa){
              tmp=ele
          }
      })
      this.setState({
          currentMahasiswa : tmp,
          viewMahasiswa: true
      })
  }

 deleteKodeMahasiswaHandler(kode_mahasiswa) {
    let tmp = {}
    this.state.mahasiswa.map((ele)=>{
        if(kode_mahasiswa == ele.kode_mahasiswa){
            tmp=ele
        }
    })
    this.setState({
        currentMahasiswa : tmp,
        deletewMahasiswa: true
    })
}

  componentDidMount() {
    this.getlistmahasiswa();
  }
  render() {
    const data = {
      columns: [
        {
          label: "No",
          field: "No",
          sort: "asc",
          width: 150
        },
        {
          label: "Kode Mahasiswa",
          field: "kode_mahasiswa",
          sort: "asc",
          width: 150
        },
        {
          label: "Nama Mahasiswa",
          field: "nama_mahasiswa",
          sort: "asc",
          width: 270
        },
        {
          label: "Alamat",
          field: "alamat",
          sort: "asc",
          width: 200
        },
        {
          label: "Kode Agama",
          field: "kode_agama",
          sort: "asc",
          width: 100
        },
        {
          label: "Kode Jurusan",
          field: "kode_jurusan",
          sort: "asc",
          width: 150
        },
        {label: "Action",
          field: "c",
          sort: "asc",
          width: 150}
        // {
        //   label: 'Salary',
        //   field: 'salary',
        //   sort: 'asc',
        //   width: 100
        // }
      ],
      rows: this.state.listmahasiswa //DATA
    };
    return <MDBDataTable striped bordered hover data={data} />;
  }
}

export default DatatablePage;
