import React from "react"
import axios from "axios"
import {Modal,ModalHeader,ModalBody,ModalFooter, Button} from "reactstrap"
import apiconfig from "../../../config/api.config.json"


class createmahasiswa extends React.Component{
    constructor(props){
        super(props)
        // Usa = ["New York", "Arizona", "Georgia", "Kentucy"];
        // Canada = ["Toronto, Vancouver"];
        // countryName = props.countryName;
        // select;
        // alert(props.countryName, "[countryName value is] from cities");
        // alert(props.hideDropdown, "[hideDropdown value is] from cities");
        // if (countryName === "none") {
        // alert(countryName, "(inside cities values of countryName is:) ");
  
        //let userdata = JSON.parse(localStorage.getItem(apiconfig.ls.userdata)) untuk update data
        this.state = {
            formdata: {
                countries: [],
                colours: {},
                kode_mahasiswa: "",
                nama_mahasiswa: "",
                alamat: "",
                kode_agama: "",
                kode_jurusan: ""
             }
        }
        this.submitHandler = this.submitHandler.bind(this)
        this.changeHandler = this.changeHandler.bind(this)
    }
    
    changeHandler(e){
        let tmp = this.state.formdata
        tmp[e.target.name] = e.target.value
        this.setState({
            formdata: tmp
        })
    }
    submitHandler(){
        let token = localStorage.getItem(apiconfig.ls.token)
        let option = {
            url: apiconfig.BASE_URL + apiconfig.ENDPOINTS.mahasiswa,
            method: "post",
            headers:{
                "Authorization": token,
                "Content-Type": "application/json"
            },
            data: this.state.formdata
        }
        axios(option)
        .then((response) => {
            if(response.data.code === 200){
                alert("sukses")
                this.props.kodemahasiswastatus()
            }
            else {
                alert(response.data.message)
                
            }
        })
        .catch((error) => {
        alert(error)
         } )
    }
    
     
    render(){
        return(
            <Modal isOpen = {this.props.create} className = {this.props.className}>
                <ModalHeader> create form</ModalHeader>
                <ModalBody>
                <form class="form-inline">
                    <div class ="input-group mb-3 input-group-sm">
                        <label for="text"> Kode Mahasiswa : </label>
                        <input type="text" class="form-control" 
                        name="kode_mahasiswa" 
                        value={this.state.formdata.kode_mahasiswa} 
                        onChange = {this.changeHandler}
                         /></div>
                         <div class ="input-group mb-3 input-group-sm">
                        <label for="text"> Nama Mahasiswa : </label>
                        <input type="text" class="form-control" placeholder="" 
                        name="nama_mahasiswa" 
                        value={this.state.formdata.nama_mahasiswa} 
                        onChange = {this.changeHandler}
                         />
                        </div>
                    {/* <div>
                    <select>
                    <option> bla bla </option>
                    {Usa.map(cname => (
                    <option>{cname}</option>
                    ))}
                    </select>
                    <p>
                    {" "}
                    hideDropdown values is {props.hideDropdown}
                    console.log(props.hideDropdown, "inside cities values of hideDrodpwn
                    is:" );
                    </p>
                    </div> */}
                    <div class ="input-group mb-3 input-group-sm"> 
                    <label for="text"> Alamat : </label>
                        <input type="text" class="form-control" placeholder="" 
                        name="alamat" 
                        value={this.state.formdata.alamat} 
                        onChange = {this.changeHandler}
                        /></div>
                         <div class ="input-group mb-3 input-group-sm">
                    <label for="text"> Kode Agama : </label>
                        <input type="text" class="form-control" placeholder="" 
                        name="kode_agama" 
                        value={this.state.formdata.kode_agama} 
                        onChange = {this.changeHandler}
                        />
                    </div>
                    
                    <div class ="input-group mb-3 input-group-sm">
                    <label for="text"> Jurusan : </label>
                        <input type="text" class="form-control" placeholder="" 
                        name="kode_jurusan" 
                        value={this.state.formdata.kode_jurusan} 
                        onChange = {this.changeHandler}
                        />
                   </div>
                   
                </form>
                </ModalBody>
                <ModalFooter>
                    <Button color = "primary" onClick = {this.submitHandler} >save </Button>
                    <Button color = "warning" onClick = {this.props.kodemahasiswastatus}> Cancel</Button>
                    <Button color = "primary" onClick = {this.submitHandler} >test </Button>
                </ModalFooter>
            </Modal>
        )
    }
}
export default createmahasiswa
