            <body>
      <div id="card">
        <div id="card-content">
          <div id="card-title">
            <label>LOGIN</label>
            <div class="underline-title"></div>
          </div>
      <form method="post" class="form">
        <label for="user-email" style={{paddingTop:22+'px'}}>
            &nbsp;Username
          </label>
        <input id="username" 
        class="form-content" 
        type="username" name="username" 
        required=""
        autoFocus=""
        value={this.state.username}
        onChange={this.textChanged}
        />

        <div class="form-border"></div>
        <label for="user-password" 
        style={{paddingTop:22+'px'}}
        >&nbsp;Password
          </label>
        <input id="user-password" 
        class="form-content" 
        type="password" 
        name="password" 
        required=""
        autoFocus=""
        value={this.state.pass}
        onChange={this.textChanged}
        />
        <div class="form-border"></div>
        <a href="#">
          <legend id="forgot-pass">Lupa Password</legend>
        </a>
        <input id="submit-btn" type="submit" name="submit" value="MASUK" 
         disabled={this.state.isRequest}
         onClick={this.onSignIn}
         />
        <a href="#" id="signup">Belum Ada Akun</a>
      </form>
    </div>
  </div>
</body>
