            <MDBTableBody>
              {this.state.po_detail.map((data, i)  => {
                return (
                  <tr key={i} style={{ borderTop: "2px solid #000066" }}>
                    <td>{data.no}</td>
                    <td>{data.kd_brg}</td>
                    <td>{data.nama_brg}</td>
                    <td>{data.qty}</td>
                    <td>{data.harga}</td>
                    <td>{data.disc}</td>
                    <td>
                      <div class="btn btn-group col-lg-8 ">
                      <button class= 'btn-group btn-dark col-md-6'
                            onClick={() => {this.createsertifikasi(data.id);}}
                            >Add
