                          <button class= 'btn-group btn-dark col-md-6'
                            onClick={() => {this.editsertifikasi(data.id);}}
                            >Edit
                          </button>
                          <button class='btn-group btn-dark col-md-6'
                            onClick={() => {this.deletesertifikasi(data.id);}}>
                            Delete
                          </button>
                        </div>
                    </td>
                  </tr>
                );
              })}
              
              
            </MDBTableBody>
          </MDBTable>
