        this.setState({ selectedOption: selectedOption.value });
    }

    changeHandler(e){
        let tmp = this.state.formdata
        tmp[e.target.name]=e.target.value
        tmp["selectedOption"]=this.state.selectedOption
        this.setState({
            formdata : tmp
        })
}
    handleChange1 = selectedOption => {
     this.setState({ selectedOption });
    };
    
    handleChange2 = selectedOption => {
    this.setState({ selectedOption2: selectedOption });
     };
    
      getProvinsi() {
        let token = localStorage.getItem(apiconfig.ls.token);
        let option = {
          url: apiconfig.BASE_URL + apiconfig.ENDPOINTS.x_sertifikasi,
          method: "get",
          headers: {
            Authorization: token
          }
        };
    
        axios(option)
          .then(response => {
            let tmp = [];
    
            response.data.message.map(row => {
              tmp.push({
                value: row.kode_provinsi,
                label: row.nama_provinsi
              });
            });
            this.setState({
              listProvinsi: tmp,
              provinsi: response.data.message
            });
          })
          .catch(error => {
            alert(error);
          });
      }
    
      getKota() {
        let token = localStorage.getItem(apiconfig.ls.token);
        let option = {
          url: apiconfig.BASE_URL + apiconfig.ENDPOINTS.x_sertifikasi,
          method: "get",
          headers: {
            Authorization: token
          }
        };
        axios(option)
          .then(response => {
            let tmp = [];
    
            response.data.message.map(row => {
              tmp.push({
                value: row.kode_kota,
                label: row.nama_kota,
                link: row.kode_provinsi
              });
            });
            this.setState({
              listKota: tmp,
              kota: response.data.message
            });
            //alert(this.state.kota);
          })
          .catch(error => {
            alert(error);
          });
      }
    
      componentDidMount() {
        this.getProvinsi();
        this.getKota();
      }
      changeHandler= event => {
        
        let tmp = this.state.formdata
        tmp[event.target.name] = event.target.value
        tmp["kode_kota"] = this.state.selectedOption2.value
       tmp["kode_provinsi"] = this.state.selectedOption.value
        this.setState({
            formdata: tmp
            
        })

    }

    validate() {

        let namerror = ""

        if (!this.state.formdata.certificate_name || !this.state.formdata.publisher || 
           !this.state.formdata.notes   ) {
            namerror = "Anda Harus Mengisi Semua Field"
        }

        if (namerror) {
            this.state.formdata.nameError = namerror
           // alert(namerror)
            return false
        }

        return true
    }


    submitHandler= event => {
        event.preventDefault();
        event.target.className += " was-validated";
        const isValid = this.validate()


        if (isValid == false) {
       //   alert(this.state.formdata.nameError)
        }else {
          let kota = this.state.selectedOption2.value
          let tmp = this.state.formdata
          tmp.kode_kota = kota
          this.setState({
              formdata : tmp
        })
        let token = localStorage.getItem(apiconfig.ls.token)
        // let selectedProductsArray = this.state.productsList.filter((product, i)=>{
        //     return product.isChecked});
        // let stringHobi = ''
        

        // selectedProductsArray.map((row) => {
        //     if (stringHobi !== '') {
        //         stringHobi = stringHobi +', ' + row.name
        //     } else {
