    //             alert("sukses")
    //             this.props.kodemahasiswastatus()
    //             alert(JSON.stringify(this.state.formdata))
    //         }
    //     })
    //     .catch((error) => {
    //     alert(error)
    //      } )
    // }
    
    render(){
      let tmp_year =[]
      this.year.map(row=> {
        tmp_year.push({
          value
        })
      })
        const options1 = this.state.listProvinsi;
        //let {productsList} =  this.state;
    const options2 = this.state.listKota;
    

  
          

        return(
            <Modal isOpen = {this.props.create}  className={"modal-dialog modal-xl"}>
                <ModalHeader style={{backgroundColor: "#000066", color: "#fff" }}> Tambah Sertifikasi</ModalHeader>
                        <form
                  className="needs-validation"
                  onSubmit={this.submitHandler}
                  noValidate
                >
                <ModalBody>
                {/* <form role="form"> */}
               
                <div class="row">
                <div class="col-lg-6">
           
              <label>Certificate Name</label>
              <input
                type="text"
                class="form-control"
                name="certificate_name"
                value={this.state.formdata.certificate_name}
                onChange={this.changeHandler}
                required
              />
              </div>
          
              <div class="col-lg-6">
              <label>Penerbit</label>
              <input
                type="text"
                class="form-control"
                name="publisher"
                onChange={this.changeHandler}
                value={this.state.formdata.publisher}
                required
              />
            </div>
            </div>
            <div class= "row">
            <div class="col">
            <p>Mulai Dari</p>
            </div>
            <div class = "col">
              <p>Sampai Dengan</p>
              
              </div>
              </div>
            
        {/* <p>Select one first</p>
        <Select
          name="kode_provinsi"
          value={this.state.selectedOption.valid_start_year}
          onChange={this.handleChange1}
          options={options1}
          required
        />
        <p>Then the other</p>
        <Select
          name="kode_kota"
          value={this.state.selectedOption2.kode_kota}
          onChange={this.handleChange2}
          options={filteredOptions}
