           <div class="col">
        
        <Select
          name="kode_provinsi"
          value={this.state.selectedOption.valid_start_year}
          onChange={this.handleChange1}
          options2
          required
        />
        </div>
        <div class ="col">
        
        <Select
          name="valid_start_month"
          value={this.state.selectedOption2.valid_start_month}
          onChange={this.handleChange2}
          options2
          required
        />
