import React from "react"
import axios from "axios"
import {Modal,ModalHeader,ModalBody,ModalFooter, Button} from "reactstrap"
import apiconfig from "../../../config/api.config.json"
import Select from 'react-select'


class editmahasiswa extends React.Component{
    constructor(props){
        super(props)
        //let userdata = JSON.parse(localStorage.getItem(apiconfig.ls.userdata)) untuk update data
        this.state = {
            formdata: {
                kode_mahasiswa: "",
                nama_mahasiswa: "",
                
                kode_agama: "",
                kode_jurusan: "",
                kode_kota: ""
             },
             provinsi: [],
             kota: [],
             listKota: [],
             listProvinsi: [],
             selectedOption: {},
             selectedOption2: {}
        }
        this.submitHandler = this.submitHandler.bind(this)
        this.changeHandler = this.changeHandler.bind(this)
    }
    componentWillReceiveProps(newProps){
        this.setState({
            formdata: newProps.listmahasiswa
        })
    }
    changeHandler(e){
        let tmp = this.state.formdata
        tmp[e.target.name] = e.target.value
        this.setState({
            formdata: tmp
        })
    }
    submitHandler(){
        let token = localStorage.getItem(apiconfig.ls.token)
        let option = {
            url: apiconfig.BASE_URL + apiconfig.ENDPOINTS.mahasiswa,
            method: "put",
            headers:{
                "Authorization": token,
                "Content-Type": "application/json"
            },
            data: this.state.formdata
        }
        axios(option)
        .then((response) => {
            if(response.data.code === 200){
                alert("sukses")
                this.props.kodemahasiswastatus()
            }
            else {
                alert(response.data.message)
                
            }
        })
        .catch((error) => {
        alert(error)
         } )
    }
    render(){
        return(
            <Modal isOpen = {this.props.edit} className = {this.props.className}>
                <ModalHeader> edit form</ModalHeader>
                <ModalBody>
                <form role="form">
            <div class="form-group">
              <label for="exampleInputEmail1">Kode Mahasiswa</label>
              <input
                type="text"
                class="form-control"
                name="kode_mahasiswa"
                value={this.state.formdata.kode_mahasiswa}
                onChange={this.changeHandler}
              />
            </div>
            <div class="form-group">
              <label for="exampleInputEmail1">Nama Mahasiswa</label>
              <input
                type="text"
                class="form-control"
                name="nama_mahasiswa"
                value={this.state.formdata.nama_mahasiswa}
                onChange={this.changeHandler}
              />
            </div>
            <div>
        <p>Select one first</p>
        <Select
          name="form-field-name"
          value={this.state.selectedOption.kode_provinsi}
          onChange={this.handleChange1}
          options={options1}
        />
        <p>Then the other</p>
        <Select
          name="form-field-name"
          value={this.state.selectedOption2.kode_kota}
          onChange={this.handleChange2}
          options={filteredOptions}
        />
      </div>
           
            <div class="form-group">
              <label for="exampleInputEmail1">Kode Agama</label>
              <input
                type="text"
                class="form-control"
                name="kode_agama"
                value={this.state.formdata.kode_agama}
                onChange={this.changeHandler}
              />
            </div>
            <div class="form-group">
              <label for="exampleInputEmail1">Kode Jurusan</label>
              <input
                type="text"
                class="form-control"
                name="kode_jurusan"
                value={this.state.formdata.kode_jurusan}
                onChange={this.changeHandler}
              />
            </div>
          </form>
                </ModalBody>
                <ModalFooter>
                    <Button color = "primary" onClick = {this.submitHandler} >save </Button>
                    <Button color = "warning" onClick = {this.props.kodemahasiswastatus}> Cancel</Button>
                </ModalFooter>
            </Modal>
        )
    }
}

export default editmahasiswa
