import React from "react";
//import ListMahasiswa from "../components/content/mahasiswa/page"
import List_sertifikasi from "../components/content/m_sertifikasi/list_sertifikasi"
// import listmahasiswa from "../components/content/mahasiswa/listmahasiswa";
// //import user from "./user";
 import Page from "../components/content/mahasiswa/page"
import DatatableSertifikasi from "../components/content/m_sertifikasi/list_sertifikasi";

// import ddd from "../components/content/controlbox/dependentdropdown"

class detailpelamar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      
      list_sertifikasi: false
    };

    this.listModalHandler = this.listModalHandler.bind(this);
   //this.listModal = this.listModal.bind(this);
    this.mStatus = this.mStatus.bind(this);
  }

  listModalHandler() {
    this.setState({
      list_sertifikasi: true
    });
  }

  // mStatus() {
  //   this.setState({
  //     listMahasiswa: false
  //   });
  //   console.log(this.state.listMahasiswa);
  //   alert(this.state.listMahasiswa);
  // }
  // listModal() {
  //   this.setState({
  //     list_sertifikasi: true
  //   });
  // }

  mStatus() {
    this.setState({
      list_sertifikasi: false
    });
   // console.log(this.state.list_sertifikasi);
  }

  componentDidMount() {
    this.mStatus();
  }
  render() {
    return (
<Modal isOpen={this.props.list} className="modal-dialog modal-xl" 
      style={{display:
        this.state.createsertifikasi == true ||
        this.state.editsertifikasi == true
          ? "none"
          : "block"}}>
      <div class="modal-header" style = {{backgroundColor:'#00008B'}}>
        <h4 class="modal-title text-white">Detail Pelamar</h4>
                 
        <button
          type="button"
          class="close text-white"
          data-dismiss="modal"
          aria-label="Close"
        >
          <span aria-hidden="true" onClick={this.props.mStatus}>
            ×
          </span>
        </button>
        
      </div>
      <div class="row">
        <div class="col-sm-4">
        <div id="list-example" class="list-group">
        <a class="list-group-item list-group-item-action" href="#list-item-1">Profil</a>
        <a class="list-group-item list-group-item-action" href="#list-item-2">Biodata</a>
        <a class="list-group-item list-group-item-action" href="#list-item-3">Pengalaman Kerja</a>
        <a class="list-group-item list-group-item-action" href="#list-item-4">Pendidikan</a>
        <a class="list-group-item list-group-item-action" href="#list-item-1">Pelatihan</a>
        <a class="list-group-item list-group-item-action" href="#list-item-2">Sertifikasi</a>
        <a class="list-group-item list-group-item-action" href="#list-item-3">Sumber Lowongan Kerja</a>
        <a class="list-group-item list-group-item-action" href="#list-item-4">Organisasi</a>
        <a class="list-group-item list-group-item-action" href="#list-item-1">Keluarga</a>
        <a class="list-group-item list-group-item-action" href="#keahlian">Keahlian</a>
        <a class="list-group-item list-group-item-action" href="#list-item-3">Lain-lain</a>
        </div>
        </div>
        <div class="col-sm-8">
          <div class ="container mt-4 mr-auto">
         
        
      </div>
           
       </div>
       <De list={this.state.list_sertifikasi} mStatus={this.mStatus} />
      </div>
     
</Modal>
  
    
    );
  }
}

export default detailpelamar;
