import React from 'react'
import api from '../helper/api'
import config from '../config/api.config.json'

class login extends React.Component{
constructor(props){
    super(props)
    this.state = {
        formdata : {
            username: '',
            password: '',
        },
        isRequest: false
    }
    this.onSignIn = this.onSignIn.bind(this)
    this.textchanged = this.textchanged.bind(this)
}
textchanged(e){
    let tmp = this.state.formdata
    tmp[e.target.name] = e.target.value
    this.setState({
        formdata: tmp
    })
}
async onSignIn(){
    this.setState({
        isRequest : true
        })
       
        let result = await api.login(this.state.formdata.username, this.state.formdata.password)
        if (result.code === 200){
        localStorage.setItem(config.ls.username, JSON.stringify(result.message.usename ))
        localStorage.setItem(config.ls.token, result.message.token)
        this.props.history.push('/home')
    }
    else {alert(result.message)
    }
    this.setState({
        isRequest:false
    })
}

    render(){
        return(
            <div className="login-page">
            <div class="login-box " >
    <div class="login-logo">
      <a href="/home">
        <b> Batch 220</b> JavaScript
      </a>
    </div>
    <div class="card">
      <div class="card-body login-card-body">
        <form>
          <div class="input-group mb-3">
            <input
              type="text" className="form-control" placeholder="Username" name="username"required="" autoFocus=""value={ this.state.username} onChange={this.textChanged}
            />
            <div class="input-group-append"> <div class="input-group-text"> <span class="fas fa-user"></span> </div>
            </div>
          </div>
          <div class="input-group mb-3">
            <input
              type="password" className="form-control" placeholder="Password" name="password" required="" value={this.state.password} onChange={this.textChanged}
            />
            <div class="input-group-append"> <div class="input-group-text"> <span class="fas fa-lock"></span></div>
            </div>
          </div>
          <div class="row">
            <div class="col-8"> <div class="icheck-primary"><input type="checkbox" id="remember" />
            <label for="remember">Remember Me</label>
              </div>
            </div>
            <div class="col-4">
              <button
                type="button" class="btn btn-primary btn-block"disabled={this.state.isRequest} onClick={this.onSignIn}
              > Sign In
              </button>
            </div>
          </div>
        </form>

        
      </div>
    </div>
  </div>
        </div>
