import React from "react";
import DatatableSertifikasi from "../m_sertifikasi"
import Detailpelamar from "./detailpelamar"

// import ddd from "../components/content/controlbox/dependentdropdown"

class sidebar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      
      detailpelamar: false,
      Datatablesertifikasi: false
    };

    this.listModalHandler = this.listModalHandler.bind(this);
   //this.listModal = this.listModal.bind(this);
    this.mStatus = this.mStatus.bind(this);
  }

  listModalHandler() {
    this.setState({
      Datatablesertifikasi: true
    });
  }


  mStatus() {
    this.setState({
      Datatablesertifikasi: false
    });
  }

  componentDidMount() {
    this.mStatus();
  }
  render() {
    return (

      <div class="container-fluid mt-5 ml-">
       
      
      <div class="row">
      
          <div class="col-md-2">
          <div class="dropdown">
          
          <a class="nav-icon fas fa-circle list-group-item list-group-item-action " href="#list-item-1">Menu 1</a>
          <a class="nav-icon fas fa-circle list-group-item list-group-item-action " href="#list-item-1">Menu 2</a>
      <button class="nav-icon fas fa-circle list-group-item list-group-item-action dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
        Menu 3
      </button>
      <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
        <a class="dropdown-item"  href="#">Action</a>
        <a class="dropdown-item" href="#">Another action</a>
        <a class="dropdown-item" href="#">Something else here</a>
      </div>
      <a class="nav-icon fas fa-circle list-group-item list-group-item-action" onClick={this.listModalHandler} href="#">Menu 4</a>
       <button class="nav-icon fas fa-circle list-group-item list-group-item-action dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
        Menu 5
      </button>
      <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
        <a class="dropdown-item" href="#">Action</a>
        <a class="dropdown-item" href="#">Another action</a>
        <a class="dropdown-item" href="#">Something else here</a>
      </div>
    </div>
          </div>

          <div class="col-md-8" style={{backgroundColor: "white" , height: '500px'}}>
            <div style={{fontSize: "30px",borderBottom: "3px solid #000066"}}>Jadwalkan Ulang </div>
            <div class="container-fluid mt-2 ">
          <div class="input-group md-form form-sm form-2 pl-0">
  <input class="form-control my-0 py-1 red-border" type="text" placeholder="Search" aria-label="Search"/>
  <div class="input-group-append">
    <span class="input-group-text red lighten-3" id="basic-text1"><i class="fa fa-button"
        aria-hidden="true">cari</i></span>  
         <div class="container-fluid ml-1 ">
        <span class="input-group-text red lighten-3" id="basic-text1"><i class="fa fa-button"
        aria-hidden="true">reset</i></span>
        </div>
  </div>
</div>
</div>
          </div> 
           
       </div>
       <DatatableSertifikasi 
       list={this.state.Datatablesertifikasi} 
       mStatus={this.mStatus} />
      </div>
     

  
    
    );
  }
}

export default sidebar;
