import React from "react"
import axios from "axios"
import {Modal,ModalHeader,ModalBody,ModalFooter, Button} from "reactstrap"
import apiconfig from "../../../config/api.config.json"


class editmahasiswa extends React.Component{
    constructor(props){
        super(props)
        //let userdata = JSON.parse(localStorage.getItem(apiconfig.ls.userdata)) untuk update data
        this.state = {
            formdata: {
                kode_mahasiswa: "",
                nama_mahasiswa: "",
                alamat: "",
                kode_agama: "",
                kode_jurusan: ""
             }
        }
        this.submitHandler = this.submitHandler.bind(this)
        this.changeHandler = this.changeHandler.bind(this)
    }
    componentWillReceiveProps(newProps){
        this.setState({
            formdata: newProps.listmahasiswa
        })
    }
    changeHandler(e){
        let tmp = this.state.formdata
        tmp[e.target.name] = e.target.value
        this.setState({
            formdata: tmp
        })
    }
    submitHandler(){
        let token = localStorage.getItem(apiconfig.ls.token)
        let option = {
            url: apiconfig.BASE_URL + apiconfig.ENDPOINTS.mahasiswa,
            method: "put",
            headers:{
                "Authorization": token,
                "Content-Type": "application/json"
            },
            data: this.state.formdata
        }
        axios(option)
        .then((response) => {
            if(response.data.code === 200){
                alert("sukses")
                this.props.kodemahasiswastatus()
            }
            else {
                alert(Response.data.message)
            }
        })
        .catch((error) => {
        console.log(error)
         } )
    }
    render(){
        return(
            <Modal isOpen = {this.props.edit} className = {this.props.className}>
                <ModalHeader> edit form</ModalHeader>
                <ModalBody>
                <form class="form-inline">
                    <div class ="input-group mb-3 input-group-sm">
                        <label for="text"> Kode Mahasiswa : </label>
                        <input type="text" class="form-control" readOnly
                        name="kode_mahasiswa" 
                        value={this.props.listmahasiswa.kode_mahasiswa} 
                         />
                        <label for="text"> Nama Mahasiswa : </label>
                        <input type="text" class="form-control" placeholder="Type Unit Name" 
                        name="nama_mahasiswa" 
                        value={this.props.listmahasiswa.nama_mahasiswa} 
                        onChange = {this.changeHandler}
                         />
                    </div>
                
                    <div class ="input-group mb-3 input-group-sm"> 
                    <label for="text"> Alamat : </label>
                        <input type="text" class="form-control" placeholder="email" 
                        name="alamat" 
                        value={this.props.listmahasiswa.alamat} 
                        onChange = {this.changeHandler}
                        />
                    <label for="text"> Kode Agama : </label>
                        <input type="text" class="form-control" placeholder="Type address" 
                        name="kode_agama" 
                        value={this.props.listmahasiswa.kode_agama} 
                        onChange = {this.changeHandler}
                        />
                    </div>
                    
                    <div class ="input-group mb-3 input-group-sm">
                    <label for="text"> Jurusan : </label>
                        <input type="text" class="form-control" placeholder="phone" 
                        name="kode_jurusan" 
                        value={this.props.listmahasiswa.kode_jurusan} 
                        onChange = {this.changeHandler}
                        />
                   </div>
                </form>
                </ModalBody>
                <ModalFooter>
                    <Button color = "primary" onClick = {this.submitHandler} >save </Button>
                    <Button color = "warning" onClick = {this.props.kodemahasiswastatus}> Cancel</Button>
                </ModalFooter>
            </Modal>
        )
    }
}

export default editmahasiswa
