import React from "react"
import axios from "axios"
import {Modal,ModalHeader,ModalBody,ModalFooter, Button} from "reactstrap"
import apiconfig from "../../../config/api.config.json"
import Select from 'react-select'


class Editsertifikasi extends React.Component{
    constructor (props){
        super(props)
       // let userdata = JSON.parse(localStorage.getItem(apiConfig.LS.USERDATA))
        this.state = {
            formdata : {
                id:'',
                create_by:'',
                create_on:'',
                modified_by:'',
                modified_on:'',
                deleted_by:'',
                deleted_on:'',
                is_delete:'',
                biodata_id: '',
                certificate_name:'',
                publisher: "",
                valid_start_year: "",
                valid_start_month: "",
                until_year: "",
                until_month: "",
                notes:''
                //update_by:userdata.username
            },
            selectedOption: {},
             selectedOption2: {},
             selectedOption3: {},
             selectedOption4: {},

        }
        this.submitHandler = this.submitHandler.bind(this)
        this.changeHandler = this.changeHandler.bind(this)
        this.handleChange1 = this.handleChange1.bind(this)
    
    }

    handleOnChange(selectedOption) {
      this.setState({ selectedOption: selectedOption.value });
  }
  handleOnChange(selectedOption2) {
    this.setState({ selectedOption2: selectedOption2.value });
  }
  handleOnChange(selectedOption3) {
    this.setState({ selectedOption3: selectedOption3.value });
  }
  handleOnChange(selectedOption4) {
    this.setState({ selectedOption4: selectedOption4.value });
  }


      componentDidMount() {
        this.getLevel();
      }


      componentWillReceiveProps(newprops) {
        this.setState({
            formdata : newprops.listkeahlian,
            selectValue: this.props.listkeahlian.skill_level_id,
        })
    }

    changeHandler= event => {
        
      let tmp = this.state.formdata
      tmp[event.target.name] = event.target.value
      tmp["valid_start_year"] = this.state.selectedOption.value
     tmp["valid_start_month"] = this.state.selectedOption2.value
     tmp["until_year"] = this.state.selectedOption3.value
     tmp["until_month"] = this.state.selectedOption4.value
      this.setState({
          formdata: tmp
          
      })

  }

    submitHandler(){
        let token = localStorage.getItem(apiconfig.ls.TOKEN)
        let option = {
            url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.KEAHLIAN, method: 'put',
            header:{
                'Authorization': token,
                'Content-Type' : 'application/json'
            },
            data:this.state.formdata
        }
        axios(option)
        .then((response) =>{
            if(response.data.code === 200) {
            alert('Success')
            this.props.modalStatus()
        } else {
            alert(response.data.message)
        }
    })

    .catch((error) => {
        console.log(error)
    })
}

render (){
    const options1 = this.state.listLevel;
    return(
        <Modal isOpen = {this.props.edit} className={this.props.className} className = "modal-dialog modal-lg"> 
        <ModalHeader className = "modal-title text-white" style = {{backgroundColor:'#00008B'}}>Edit Keahlian</ModalHeader>
        <ModalBody>
        <form>
                  <div class="row">
                    <div class="col">
                      <label for="skill_name">Nama Keahlian</label>
                            <input
                                type="text"
                                class="form-control"
                                name="skill_name"
                                value={this.props.listkeahlian.skill_name}
                                onChange={this.changeHandler}
                            />
                        </div>
                          <div class="col">
                          <label for="skill_level_id"> Level Kemampuan : </label>
                            <Select
                            placeholder="Level Kemampuan" 
                            name="skill_level_id"
                            value={options1.filter(({ value }) => value === this.props.listkeahlian.skill_level_id)}
                            onChange={this.handleChange1}
                            options={options1}
                            />
                        </div>
                      </div>
                    </form>

                    <div class="form-group">
                    <label for="comment">Catatan</label>
                    <textarea class="form-control" 
                    name="notes"
                    rows="5" id="comment"
                    value={this.props.listkeahlian.notes}
                    onChange={this.changeHandler}
                    />
                  </div>
            {/* <form role="form">
                    <div class="form-group">
                    <label for="skill_name">Skill Name</label>
                    <input
                        type="text"
                        class="form-control"
                        name="skill_name"
                        value={this.props.listkeahlian.skill_name}
                        onChange = {this.changeHandler}
                        />
                    </div>

                    <div class="form-group">
                    <label for="skill_level_id"> Level Kemampuan : </label>
                            <Select
                            placeholder="Level Kemampuan" 
                            name="skill_level_id"
                            value={options1.filter(({ value }) => value === this.props.listkeahlian.skill_level_id)}
                            onChange={this.handleChange1}
                            options={options1}
                            />
                    </div>

                    <div class="form-group">
                    <label for="notes">Notes</label>
                    <input
                        type="text"
                        class="form-control"
                        name="notes"
                        value={this.props.listkeahlian.notes}
                        onChange = {this.changeHandler}
                        />
                    </div>

                    </form> */}

                <ModalFooter>
                    <Button style = {{backgroundColor:'#FFA500'}} onClick = {this.props.modalStatus}>Close</Button>
                    <Button style = {{backgroundColor:'#00008B'}} onClick = {this.submitHandler}>Save</Button>
                </ModalFooter>
            </ModalBody>
        </Modal>

    )
}
}
export default Editsertifikasi
