import React from "react";
import { Modal,ModalHeader,ModalBody,ModalFooter, Button } from "reactstrap";
import { MDBTable, MDBTableBody,MDBTableHead} from "mdbreact";
// import Editsertifikasi from "../components/content/m_sertifikasi/edit_sertifikasi"
// import Createsertifikasi from "../components/content/m_sertifikasi/create_sertifikasi"
// import Deletesertifikasi from "../components/content/m_sertifikasi/delete_sertifikasi"
import apiconfig from '../../../config/api.config.json'
import axios from "axios";

class Bri extends React.Component {
 
  render() {
    
    return (
      <Modal isOpen={this.props.view} className={this.props.className}>
      <ModalHeader> View Unit </ModalHeader> 
      <ModalBody>

      <form class="form-inline">
              <div class ="input-group mb-3 input-group-sm">
                  <label for="text"> id : </label>
                  <input type="text" class="form-control" readOnly
                  name="id" 
                  value={this.statepo_detail.kd_trans}
                   />
                  <label for="text"> username : </label>
                  <input type="text" class="form-control" placeholder="Type Unit Name" readOnly
                  name="username" 
                  value={this.props.po_detail.tgl_trans}
                   />
              </div>
          
              <div class ="input-group mb-3 input-group-sm"> 
              <label for="text"> m_password : </label>
                  <input type="text" class="form-control" placeholder="email" readOnly
                  name="m_password" 
                  value={this.props.po_detail.total_item}
                  />
              </div>
          </form>

      </ModalBody>

      <ModalFooter>
          <Button color="danger" onClick= {this.props.idstatus}> Close </Button>
      </ModalFooter>
  </Modal>
    );
  }
}

export default Bri;
