import React from "react";
import Datatablesertifikasi from "../components/content/m_sertifikasi/list_sertifikasi";
import { Route } from "react-router-dom";
import { NavLink, BrowserRouter } from "react-router-dom";
import { Modal, ModalBody } from "reactstrap";
import { MDBDataTable,MDBTable, MDBTableBody,MDBTableHead, MDBTableFoot} from "mdbreact";
import Editsertifikasi from "../components/content/m_sertifikasi/edit_sertifikasi"
import Createsertifikasi from "../components/content/m_sertifikasi/create_sertifikasi"
import Deletesertifikasi from "../components/content/m_sertifikasi/delete_sertifikasi"
import apiconfig from '../config/api.config.json'
import axios from "axios";

class Bri extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      x_sertifikasi: [],
              list_sertifikasi: [],
              current_sertifikasi: {}
    };
  
    //this.listModalbri = this.listModalbri.bind(this);
    this.mStatus = this.mStatus.bind(this);
    this.editsertifikasi = this.editsertifikasi.bind(this)
    this.deletesertifikasi = this.deletesertifikasi.bind(this)
    this.sertifikasistatus = this.sertifikasistatus.bind(this)
    this.createsertifikasi = this.createsertifikasi.bind(this)
    this.Viewsertifikasi = this.Viewsertifikasi.bind(this)
  }

  // listModalbri() {
  //   this.setState({
  //     list_sertifikasi: true
  //   });
  // }
  sertifikasistatus(){
    this.setState({
      Viewsertifikasi: false,
      editsertifikasi: false,
      deletesertifikasi: false,
      createsertifikasi: false
    })
    this.getx_sertifikasi()
  }
  createsertifikasi(){
    this.setState({
      createsertifikasi:true
    })
  }
  mStatus() {
    this.setState({
      list_sertifikasi: false
    });
  }
  editsertifikasi(id){
    let tmp = {}
    this.state.x_sertifikasi.map((ele) =>{
      if(id == ele.id){
       tmp = ele
      }
    })
      this.setState({
        current_sertifikasi : tmp,
        editsertifikasi : true
    })
  }
  Viewsertifikasi(id) {
    let tmp = {};

    this.state.x_sertifikasi.map(ele => {
      if (id == ele.id) {
        tmp = ele;
      }
    });

    this.setState({
      current_sertifikasi: tmp,
      viewsertifikasi: true
    });
  }
  getx_sertifikasi(){
    let token = localStorage.getItem(apiconfig.ls.token);
    let option = {
       url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.x_sertifikasi,
      method: "get",
      headers : {
        Authorization : token
      }
    };
    axios(option)
    .then(response => {
      this.setState({
        // list_sertifikasi:tmp,
        x_sertifikasi: response.data.message
    });
  })
    .catch(error =>{
     alert(error)
    })
  }
  deletesertifikasi(id) {
    this.state.x_sertifikasi.map((ele)=>{
        if(id == ele.id){
           this.setState({
             current_sertifikasi : ele,
             deletesertifikasi : true
           })
        }
    })
  }
  componentDidMount() {
    this.getx_sertifikasi();
  }
  
  render() {
    return (
<Modal
      isOpen={this.props.list}
        className="modal-dialog modal-xl"
        style={{
          border: "3px solid #000066",
          boxShadow: "none"
        }}
      >
        <div class="modal-header" style={{ backgroundColor: "#000066" }}>
          <h4 class="modal-tittle w-100 text-center" style={{ color: "#fff" }}>
            Pemasukan Data Pembuatan PO
          </h4>
          <button
            type="button"
            class="close"
            data-dismiss="modal"
            aria-label="Close"
            onClick={this.props.mStatus}
          >
            <span aria-hidden="true" style={{ color: "#fff" }}>
              X
            </span>
          </button>
        </div>
        
          
          <div class="container">
  <div class="row justify-content-md-center">
    <div class="col-md-auto">
    <label> KODE TRANSAKSI</label>
      <input type="text" />
    </div>
    <div class="col-md-auto">
     <label>TNGL TRANS</label>
      <input type="text"/>
    </div>
    </div>
    <div class="row justify-content-md-center">
    <div class="col-md-auto">
      <label>TOTAL ITEM BARANG</label>
      <input type="text" />
    </div>
    <div class="col-md-auto">
    <label>TOTAL HARGA</label>
      <input type="text"/>
    </div>
    </div>
    <div class="row justify-content-md-center">
    <div class="col-md-auto">
      <label>DISCOUNT</label>
      <input type="text" />
    </div>
    <div class="col-md-auto">
     <label>KODE SUPLIER</label>
      <input type="text"/>
    </div>
    </div>
	</div>
  <MDBTable striped>
          <MDBTableHead cstyle={{ borderTop: "3px solid #000066" }}>
              <tr style={{ borderBottom: "3px solid #000066" }}>
                  <th>Nama Certifikasi</th>
                      <th>Penerbit</th>
                       <th>Masa Berlaku Sertifikasi</th>
                       <th>#</th>
                     </tr>
                     </MDBTableHead>
            <MDBTableBody>
              {this.state.x_sertifikasi.map((data, i) => {
                return (
                  <tr key={i} style={{ borderTop: "2px solid #000066" }}>
                    <td>{data.certificate_name}</td>
                    <td>{data.publisher}</td>
                    <td>
                      {data.valid_start_month}/{data.valid_start_year} s.d {data.until_month}/{data.until_year}
                    </td>
                    <td>
                      <div class="btn-group">
                        <button
                          type="button"
                          class="btn btn-default"
                          data-toggle="dropdown"
                          aria-expanded="false"
                          style={{
                            backgroundColor: "#000066",
                            color: "#fff"
                          }}
                        >
                          {" "}
                          <span>More</span>{" "}
                          <li className="fas fa-caret-down"></li>
                        </button>

                        <span class="sr-only">Toggle Dropdown</span>
                        <div
                          class="dropdown-menu"
                          role="menu"
                          x-placement="bottom-start"
                        >
                          <button
                            class="dropdown-item"
                            onClick={() => {
                              this.editsertifikasi(data.id);
                            }}
                          >
                            Ubah
                          </button>
                          <div class="dropdown-divider"></div>
                          <button
                            class="dropdown-item"
                            type="button"
                            onClick={() => {
                              this.deletesertifikasi(data.id);
                            }}
                          >
                            Hapus
                          </button>
                        </div>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </MDBTableBody>
            <MDBTableFoot>
            <div class="container-fluid">
            <div class="btn btn-group">
              <button type= "button" class= "btn btn-sm btn-dark btn-col-">submit</button>
               <div class="col-6">
               <button type= "button" class= "btn btn-sm btn-dark">cancel</button>
              </div>
            </div>
            </div>
            </MDBTableFoot>
          </MDBTable>
          
          <Editsertifikasi
            edit={this.state.editsertifikasi}
            sertifikasistatus={this.sertifikasistatus}
            list_sertifikasi={this.state.current_sertifikasi}
          />
          <Deletesertifikasi
            delete={this.state.deletesertifikasi}
            sertifikasistatus={this.sertifikasistatus}
            list_sertifikasi = {this.state.current_sertifikasi}/>
          
          <Createsertifikasi
            create={this.state.createsertifikasi}
            sertifikasistatus={this.sertifikasistatus}
          />
            {/* <MDBDataTable striped bordered hover data = {data}/> */}        
      </Modal>
    );
  }
}

export default Bri;
