import React from 'react'
import api from '../helper/api'
import config from '../config/api.config.json'

class login extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            formdata : {
                userid: '',
                Password: '',
            },
            isRequest: false
        }
        this.onSignIn = this.onSignIn.bind(this)
        this.textchanged = this.textchanged.bind(this)
    }
    textchanged(e){
        let tmp = this.state.formdata
        tmp[e.target.name] = e.target.value
        this.setState({
            formdata: tmp
        })
    }
    async onSignIn(){
        this.setState({
            isRequest : true
            })
           
            let result = await api.login(this.state.formdata.userid, this.state.formdata.Password)
            if (result.code === 200){
            localStorage.setItem(config.ls.userid, JSON.stringify(result.message.userid ))
            localStorage.setItem(config.ls.token, result.message.token)
            this.props.history.push('/Dashboard')
        }
        else {alert(result.message)
        }
        this.setState({
            isRequest:false
        })
    }
    
        render(){
            return(
              
              <center>
            
              <div class = "login-box mt-5" style = {{ border : "outset" }}>
                 <div class="card" >
                <div class="card-body login-card-body">
                <form className = "form-signin">
                   <center> <h1 className = "h3 mb-9 front-widget-normal-center"> SISTEM INFORMASI INVENTORI</h1></center>
                   <p>SILAHKAN MASUK KE APLIKASI INVENTORI</p>
                    <label for = "USER ID" className = "sr-only"> USER ID</label>
                    <div class="input-group mb-3">
                    <input type = "text" className = "form-control" placeholder = "userid" name = "userid" required = "" autofosus = "" value = { this.state.userid} onChange={this.textchanged} />
                    <div class="input-group-append"> <div class="input-group-text"> <span class="fas fa-user"></span> </div></div></div>
                    <label for = "Password" className = "sr-only">PASSWORD</label>
                    <div class="input-group mb-3">
                    <input type = "Password" className = "form-control" placeholder = "Password" name = "Password" required = "" value = { this.state.Password} onChange={this.textchanged} />
                    <div class="input-group-append"> <div class="input-group-text"> <span class="fas fa-lock"></span></div>
                    </div>
                    </div>
                    <button className = "btn btn-lg btn-primary btn-block" disabled ={this.state.isRequest} type = "button" onClick = {this.onSignIn}>LOGIN</button>
    
                    </form>
                    </div>
                    </div>
                    </div>
                    </center>
                    )
                }
    }
export default login
