import React from "react"
import axios from "axios"
import {Modal,ModalHeader,ModalBody,ModalFooter, Button} from "reactstrap"
import apiconfig from "../../../config/api.config.json"
import Select from 'react-select'


class editsertifikasi extends React.Component{
    constructor (props){
        super(props)
        const year = new Date().getFullYear()
        this.years = Array.from(new Array(50), (val, index) => year - index)
       // let userdata = JSON.parse(localStorage.getItem(apiConfig.LS.USERDATA))
        this.state = {
            formdata : {
                id:'',
                create_by:'',
                create_on:'',
                modified_by:'',
                modified_on:'',
                deleted_by:'',
                deleted_on:'',
                is_delete:'',
                biodata_id: '',
                certificate_name:'',
                publisher: "",
                valid_start_year: "",
                valid_start_month: "",
                until_year: "",
                until_month: "",
                notes:''
                //update_by:userdata.username
            },
            selectedOption: {},
             selectedOption2: {},
             selectedOption3: {},
             selectedOption4: {},

        }
        this.submitHandler = this.submitHandler.bind(this)
        this.changeHandler = this.changeHandler.bind(this)
        this.handleOnChange = this.handleOnChange.bind(this)
    }

    handleOnChange(selectedOption) {
      this.setState({ selectedOption: selectedOption.value });
  }
  handleOnChange(selectedOption2) {
    this.setState({ selectedOption2: selectedOption2.value });
  }
  handleOnChange(selectedOption3) {
    this.setState({ selectedOption3: selectedOption3.value });
  }
  handleOnChange(selectedOption4) {
    this.setState({ selectedOption4: selectedOption4.value });
  }


      // componentDidMount() {
      //   this.getlist_sertifikasi()
      // }


      componentWillReceiveProps(newprops) {
        this.setState({
            formdata : newprops.list_sertifikasi,
            
        })
    }

    changeHandler= event => {
        
      let tmp = this.state.formdata
      tmp[event.target.name] = event.target.value
      tmp["valid_start_year"] = this.state.selectedOption.value
     tmp["valid_start_month"] = this.state.selectedOption2.value
     tmp["until_year"] = this.state.selectedOption3.value
     tmp["until_month"] = this.state.selectedOption4.value
      this.setState({
          formdata: tmp
          
      })

  }

    submitHandler(){
      let token = localStorage.getItem(apiconfig.ls.token)
        let options = {
          url: apiconfig.BASE_URL + apiconfig.ENDPOINTS.x_sertifikasi, 
            method: 'put',
            header:{
                'Authorization': token,
                'Content-Type' : 'application/json'
            },
            data:this.state.formdata
        }
        axios (options)
        .then((response) => {
            if(response.data.code === 200){
                alert ("Sukses")
                this.props.sertifikasistatus()
              
                // window.location.reload()
            }
          
        })

    .catch((error) => {
        console.log(error)
    })
}

render (){
  let tmp_year =[]
  this.years.map(row=> {
    tmp_year.push({
      value: row,
      label: row
    })
  })
    const options1 = tmp_year;
    //let {productsList} =  this.state;

const options3 = tmp_year;

const month_options1 = [
  { value: "1", label: "January" },
  { value: "2", label: "Febuary" },
  { value: "3", label: "Maret" },
  { value: "4", label: "April" },
  { value: "5", label: "Mei" },
  { value: "6", label: "Juni" },
  { value: "7", label: "Juli" },
  { value: "8", label: "Agustus" },
  { value: "9", label: "September" },
  { value: "10", label: "Ocktober" },
  { value: "11", label: "November" },
  { value: "12", label: "Desember" }
];
const month_options2 = [
  { value: "1", label: "January" },
  { value: "2", label: "Febuary" },
  { value: "3", label: "Maret" },
  { value: "4", label: "April" },
  { value: "5", label: "Mei" },
  { value: "6", label: "Juni" },
  { value: "7", label: "Juli" },
  { value: "8", label: "Agustus" },
  { value: "9", label: "September" },
  { value: "10", label: "Ocktober" },
  { value: "11", label: "November" },
  { value: "12", label: "Desember" }
];
    return(
        <Modal isOpen = {this.props.edit} className={this.props.className} className = "modal-dialog modal-lg"> 
        <ModalHeader className = "modal-title text-white" style = {{backgroundColor:'#00008B'}}>Edit Sertifikasi</ModalHeader>
        <ModalBody>
                {/* <form role="form"> */}
               
                <div class="row">
                <div class="col-lg-6">
           
              <label>Certificate Name</label>
              <input
                type="text"
                class="form-control"
                name="certificate_name"
                value={this.state.formdata.certificate_name}
                onChange={this.changeHandler}
                required
              />
              </div>
          
              <div class="col-lg-6">
              <label>Penerbit</label>
              <input
                type="text"
                class="form-control"
                name="publisher"
                onChange={this.changeHandler}
                value={this.state.formdata.publisher}
                required
              />
            </div>
            </div>
            <div class= "row">
            <div class="col">
            <p>Mulai Dari</p>
            </div>
            <div class = "col">
              <p>Sampai Dengan</p>
              
              </div>
              </div>
            
        {/* <p>Select one first</p>
        <Select
          name="kode_provinsi"
          value={this.state.selectedOption.valid_start_year}
          onChange={this.handleChange1}
          options={options1}
          required
        />
        <p>Then the other</p>
        <Select
          name="kode_kota"
          value={this.state.selectedOption2.kode_kota}
          onChange={this.handleChange2}
          options={filteredOptions}
          required
        />
      </div> */}
           
           <div class = "row">
           <div class="col">
        
        <Select
          name="kode_provinsi"
          value={this.state.selectedOption.valid_start_year}
          value={options1.valid_start_year(({value}) => value === this.props.listmahasiswa.kode_provinsi)}
          onChange={this.handleChange1}
          options = {options1}
          required
        />
        </div>
        <div class ="col">
        
        <Select
          name="valid_start_month"
          value={this.state.selectedOption2.valid_start_month}
          onChange={this.handleChange2}
          options = {month_options1}
          required
        />
        </div>
      
      <div class="col">
        
        <Select
          name="until_year"
          value={this.state.selectedOption3.until_year}
          onChange={this.handleChange3}
          options = {options3}
          required
        />
        </div>
        
        <div class="col">
        
        <Select
          name="until_month"
          value={this.state.selectedOption4.until_month}
          onChange={this.handleChange4}
          options = {month_options2}
          required
        />
      </div>
      </div>
    
           <div class="form-group">
              <label>Notes</label>
              <input
                type="text"
                class="form-control"
                name="notes"
                value={this.state.formdata.notes}
                onChange={this.changeHandler}
                required
              />
            </div>
                <ModalFooter>
                    <Button style = {{backgroundColor:'#FFA500'}} onClick = {this.props.sertifikasistatus}>Close</Button>
                    <Button style = {{backgroundColor:'#00008B'}}  type = {this.submitHandler} >Save</Button>
                </ModalFooter>
            </ModalBody>
        </Modal>

    )
}
}
export default editsertifikasi
