import React from "react";
import apiconfig from '../../../config/api.config.json'
import axios from "axios";
import { Link } from "react-router-dom";
import ViewMahasiswa from "./viewm_user"
import CreateMahasiswa from "./createm_user"
import EditMahasiswa from "./editm_user"
import DeleteMahasiswa from "./deletem_user"
import { MDBDataTable } from "mdbreact";

class View {}

class m_user extends React.Component {
  constructor(props){
              super(props);
              this.state={
                m_user: [],
              listm_user: [],
              currentm_user: {}
            };
            this.viewidHandler = this.viewidHandler.bind(this)
            this.editidHandler = this.editidHandler.bind(this)
            this.deleteidaHandler = this.deleteidHandler.bind(this)
            this.kodeidstatus = this.kodeidstatus.bind(this)
            this.createid = this.createid.bind(this)
          }
          createid(){
            this.setState({
              createid:true
            })
          }

          idstatus(){
            this.setState({
              Viewida: false,
              editmahasiswa: false,
              deletemahasiswa: false,
              createmahasiswa: false
            })
            this.getlistmahasiswa()
          }

          editkodemahasiswaHandler(kode_mahasiswa){
            this.state.mahasiswa.map((ele) =>{
              if(kode_mahasiswa == ele.kode_mahasiswa){
                this.setState({
                  currentmahasiswa: ele,
                  editmahasiswa: true
                })
              }
            })
          }

            getlistmahasiswa(){
              let token = localStorage.getItem(apiconfig.ls.token);
              let option = {
                 url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.mahasiswa,
                method: "get",
                headers : {
                  Authorization : token
                }
              };
              axios(option)
              .then(response => {
               let tmp =[]
               response.data.message.map((row,x) => {
                 let c = <Link to="#">
                 <span onClick= {() => {this.viewkodemahasiswaHandler(row.kode_mahasiswa)}} className = "fa fa-search"
                 style= {{frontSize: "18px", paddingRight: "30px"}}/>
                 <span onClick= {() => {this.editkodemahasiswaHandler(row.kode_mahasiswa)}} className = "fa fa-edit"
                 style= {{frontSize: "18px", paddingRight: "30px"}}/>
                 <span onClick= {() => {this.deletekodemahasiswaHandler(row.kode_mahasiswa)}} className = "fa fa-trash"
                 style= {{frontSize: "18px"}}/>
                 </Link>
                 tmp.push({"No":x+1, "kode_mahasiswa": row.kode_mahasiswa,"nama_mahasiswa":row.nama_mahasiswa, "alamat": row.alamat, "kode_agama": row.kode_agama, 
                 "kode_jurusan": row.kode_jurusan,c})
                })
                this.setState({
                  listmahasiswa:tmp,
                  mahasiswa: response.data.message
              });
            })
              .catch(error =>{
                alert(error)
              })
            }
            viewkodemahasiswaHandler(kode_mahasiswa) {
              // alert(kode_mahasiswa)
              let tmp = {}
              this.state.mahasiswa.map((ele)=>{
                  if(kode_mahasiswa == ele.kode_mahasiswa){
                      tmp=ele
                  }
              })
              this.setState({
                  currentmahasiswa : tmp,
                  Viewmahasiswa: true
              })
              // alert(JSON.stringify(tmp))
              // alert(JSON.stringify(this.state.currentmahasiswa))
          }

        
         deletekodemahasiswaHandler(kode_mahasiswa) {
            this.state.mahasiswa.map((ele)=>{
                if(kode_mahasiswa == ele.kode_mahasiswa){
                   this.setState({
                     currentmahasiswa : ele,
                     deletemahasiswa : true
                   })
                }
            })
          }
        
          componentDidMount() {
            this.getlistmahasiswa();
          }
  render() {
    const data = {
      columns: [
        {
          label: "No",
          field: "No",
          sort: "asc",
          width: 150
        },
        {
          label: "Kode Mahasiswa",
          field: "kode_mahasiswa",
          sort: "asc",
          width: 150
        },
        {
          label: "Nama Mahasiswa",
          field: "nama_mahasiswa",
          sort: "asc",
          width: 270
        },
        {
          label: "Alamat",
          field: "alamat",
          sort: "asc",
          width: 200
        },
        {
          label: "Kode Agama",
          field: "kode_agama",
          sort: "asc",
          width: 100
        },
        {
          label: "Kode Jurusan",
          field: "kode_jurusan",
          sort: "asc",
          width: 150
        },
        {label: "Action",
          field: "c",
          sort: "asc",
          width: 150}
        // {
        //   label: 'Salary',
        //   field: 'salary',
        //   sort: 'asc',
        //   width: 100
        // }
      ],
      rows: this.state.listmahasiswa //DATA
    };
    return(
      <div>
        <button type = "button" class="btn btn-primary float-right"
        onClick={this.createmahasiswa}> add </button>
        <CreateMahasiswa
        create = {this.state.createmahasiswa}
        kodemahasiswastatus = {this.kodemahasiswastatus}
        listmahasiswa = {this.state.currentmahasiswa}/>
            <ViewMahasiswa 
            listmahasiswa = {this.state.currentmahasiswa}
            view = {this.state.Viewmahasiswa}
            kodemahasiswastatus = {this.kodemahasiswastatus}/>

            <EditMahasiswa
            edit = {this.state.editmahasiswa}
            kodemahasiswastatus = {this.kodemahasiswastatus}
            listmahasiswa = {this.state.currentmahasiswa}/>
       
            <DeleteMahasiswa
            delete = {this.state.deletemahasiswa}
            kodemahasiswastatus = {this.kodemahasiswastatus}
            listmahasiswa = {this.state.currentmahasiswa}/>
        
        
        <MDBDataTable striped bordered hover data={data} />
         </div> 
         )
  }
}

export default m_user;
