import React from "react";
import apiconfig from '../../../config/api.config.json'
import axios from "axios";
import { Link } from "react-router-dom";
import { MDBDataTable } from "mdbreact";

class View {}

class DatatablePage extends React.Component {
  constructor(props){
              super(props)
              this.state={
                mahasiswa: [],
              listmahasiswa: []
              
            }
          }

            getlistmahasiswa(){
              let token = localStorage.getItem(apiconfig.ls.token)
              let option = {
                 url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.mahasiswa,
                method: "get",
                headers : {
                  "Authorization" : token
                }
              }
              axios(option)
              .then((response) => {
                this.setState({
                  listmahasiswa: response.data.message
                })
              })
              .catch((error) =>{
                alert(error)
              })
            }

            componentDidMount(){
              this.getlistmahasiswa()
            }
  render() {
    const data = {
      columns: [
        {
          label: "No",
          field: "No",
          sort: "asc",
          width: 150
        },
        {
          label: "Kode Mahasiswa",
          field: "kode_mahasiswa",
          sort: "asc",
          width: 150
        },
        {
          label: "Nama Mahasiswa",
          field: "nama_mahasiswa",
          sort: "asc",
          width: 270
        },
        {
          label: "Alamat",
          field: "alamat",
          sort: "asc",
          width: 200
        },
        {
          label: "Kode Agama",
          field: "kode_agama",
          sort: "asc",
          width: 100
        },
        {
          label: "Kode Jurusan",
          field: "kode_jurusan",
          sort: "asc",
          width: 150
        },
        {label: "Action",
          field: "Action",
          sort: "asc",
          width: 150}
        // {
        //   label: 'Salary',
        //   field: 'salary',
        //   sort: 'asc',
        //   width: 100
        // }
      ],
      rows: this.state.listmahasiswa //DATA
    };
    return <MDBDataTable striped bordered hover data={data} />;
  }
}

export default DatatablePage;
