                type="text"
                class="form-control"
                name="publisher"
                onChange={this.changeHandler}
                value={this.state.formdata.publisher}
                required
              />
            </div>
            </div>
            <div class= "row">
            <div class="col">
            <p>Mulai Dari</p>
            </div>
            <div class = "col">
              <p>Sampai Dengan</p>
              
              </div>
              </div>
            
