import React from "react";
import apiconfig from '../../../config/api.config.json'
import axios from "axios";
import { Link } from "react-router-dom";
import ViewSertifikasi from "./view_sertifikasi"
import Createsertifikasi from "./create_sertifikasi"

import Deletesertifikasi from "./delete_sertifikasi"
import { MDBDataTable } from "mdbreact";
import { Modal, ModalBody, ModalFooter, ModalHeader, Button } from "reactstrap"; 
// import Test from "./Test";
import{MDBTable, MDBTableBody,MDBTableHead} from "react"

class View {}

class DatatableSertifikasi extends React.Component {
  constructor(props){
              super(props);
              this.state={
                x_sertifikasi: [],
      
              list_sertifikasi: [],
              current_setifikasi: {}

            };
            this.viewsertifikasiHandler = this.viewsertifikasiHandler.bind(this)
            //this.editsertifikasiHandler = this.editsertifikasiHandler.bind(this)
            this.deletesertifikasiHandler = this.deletesertifikasiHandler.bind(this)
            this.sertifikasistatus = this.sertifikasistatus.bind(this)
            this.createsertifikasi = this.createsertifikasi.bind(this)
          //  this.getListHobi = this.getListHobi.bind(this)
            // this.test = this.test.bind(this);
            
          }

       
        //   getListHobi () {
        //     let productsList  = [
        //         {name: 'Bermain', isChecked: false},
        //             {name: 'Makan', isChecked: false},
        //             {name: 'Tidur', isChecked: false},
        //             {name: 'Mager', isChecked: false},
        //       ]
    
        //       return productsList
        // }
          createsertifikasi(){
            this.setState({
              createsertifikasi:true
            })
          }
          sertifikasistatus(){
            this.setState({
              Viewsertifikasi: false,
              editsertifikasi: false,
              deletesertifikasi: false,
              createsertifikasi: false
            })
            this.getlist_sertifikasi()
          }

        //   editsertifikasiHandler(id){
        //     let tmp = {}
        //     let tmpHobi = this.getListHobi()
        //     this.state.mahasiswa.map((ele) =>{
        //       if(kode_mahasiswa == ele.kode_mahasiswa){
        //        tmp = ele
        //       }
        //     })
        //       let array = tmp.hobi.split(',')
         
        //       tmpHobi.map((rows) => {
                  
        //           array.forEach(ele =>{
        //               if (ele.trim() == rows.name){
        //                   rows.isChecked = !rows.isChecked 
        //               }
                  
        //       })
            
              
        //       })
        //       this.setState({
        //         currentmahasiswa : tmp,
        //         editmahasiswa : true,
        //         productsListNew : tmpHobi
        //     })
          
        //   }

            getlist_sertifikasi(){
              let token = localStorage.getItem(apiconfig.ls.token);
              let option = {
                 url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.x_ser,
                method: "get",
                headers : {
                  Authorization : token
                }
              };
              axios(option)
              .then(response => {
            let tmp =[]
               response.data.message.map((row,x) => {
                 let c = 
                <div class="dropdown">
                        <span class="btn btn-secondary dropdown-toggle" className="fas fa-th-large" id="dropdownMenu2" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false" />

                            <div class="dropdown-menu" aria-labelledby="dropdownMenu2">
                                <Link to='#'>
                                    <button class="dropdown-item" type="button"><span onClick={() => { this.viewsertifikasiHandler(row.id) }} className="fa fa-eye" style={{ fontSize: 'px', paddingRight: '30px' }}>&nbsp;&nbsp;&nbsp;View </span></button>
                                    <button class="dropdown-item" type="button"><span onClick={() => { this.editsertifikasiHandler(row.id) }} className="fa fa-edit" style={{ fontSize: 'px', paddingRight: '30px' }}>&nbsp;&nbsp;&nbsp;Edit</span></button>
                                    <button class="dropdown-item" type="button"><span onClick={() => { this.deletesertifikasiHandler(row.id) }} className="fa fa-trash" style={{ fontSize: 'px' }}>&nbsp;&nbsp;&nbsp;&nbsp;Delete</span></button>
                                </Link>
                            </div>
                    </div >
                
                 {/* <span onClick= {() => {this.viewkodemahasiswaHandler(row.kode_mahasiswa)}} className = "fa fa-search"
                 style= {{frontSize: "18px", paddingRight: "30px"}}/>
                 <span onClick= {() => {this.editkodemahasiswaHandler(row.kode_mahasiswa)}} className = "fa fa-edit"
                 style= {{frontSize: "18px", paddingRight: "30px"}}/>
                 <span onClick= {() => {this.deletekodemahasiswaHandler(row.kode_mahasiswa)}} className = "fa fa-trash"
                 style= {{frontSize: "18px"}}/> */}
                //  </Link>
                 tmp.push({"No":x+1, "certificate_name": row.certificate_name,"publihser":row.publisher, "valid_start_year": row.valid_start_year, "valid_start_month": row.valid_start_month, 
                 "until_year": row.until_year,"until_month": row.until_month,"notes":row.notes,c})
                })
                //alert(JSON.stringify(tmp))
                this.setState({
                  list_sertifikasi:tmp,
                  x_sertifikasi: response.data.message
              });
            })
              .catch(error =>{
                alert(error)
              })
            }
            viewsertifikasiHandler(id) {
              let tmp = {}
            //  let tmpHobi = this.getListHobi()
              this.state.x_sertifikasi.map((ele)=>{
                  if(id == ele.id){
                      tmp=ele
                  }
              })
            //   let array = tmp.hobi.split(',')
         
            //   tmpHobi.map((rows) => {
                  
            //       array.forEach(ele =>{
            //           if (ele.trim() == rows.name){
            //               rows.isChecked = !rows.isChecked
                          
            //           }
            //       })
            //   })
              this.setState({
                  current_sertifikasi : tmp,
                  Viewsertifikasi: true,
                 // productsListNew : tmpHobi
              })
          }
          // test() {
          //   this.setState({
              
          //     test: true
          //   });
          // }

        
         deletesertifikasiHandler(id) {
            this.state.x_sertifikasi.map((ele)=>{
                if(id == ele.id){
                   this.setState({
                     currentx_sertifikasi : ele,
                     deletesertifikasi : true
                   })
                }
            })
          }
        
          componentDidMount() {
            this.getlist_sertifikasi();
          }
  render() {
    const data = {
      columns: [
        {
          label: "No",
          field: "No",
          sort: "asc",
          width: 50
        },
        {
          label: "certificate_name",
          field: "certificate_name",
          sort: "asc",
          width: 50
        },
        {
          label: "publisher",
          field: "publisher",
          sort: "asc",
          width: 270
        },
        {
          label: "valid_start_year",
          field: "valid_start_year",
          sort: "asc",
          width: 50
        },
        {
          label: "valid_start_month",
          field: "valid_start_month",
          sort: "asc",
          width: 50
        },
        {
          label: "until_year",
          field: "until_year",
          sort: "asc",
          width: 50
        },
        {
          label: "until_month",
          field: "until_month",
          sort: "asc",
          width: 50
        },
        {
          label: "notes",
          field: "notes",
          sort: "asc",
          width: 50
        },
        {label: "Action",
          field: "c",
          sort: "asc",
          width: 50}
        // {
        //   label: 'Salary',
        //   field: 'salary',
        //   sort: 'asc',
        //   width: 100
        // }
      ],
      rows: this.state.list_sertifikasi //DATA
    };
    return(
      <Modal isOpen={this.props.list} className="modal-dialog modal-xl">
      <div class="modal-header" style = {{backgroundColor:'#00008B'}}>
        <h4 class="modal-title text-white">Sertifikasi</h4>
                 
        <button
          type="button"
          class="close text-white"
          data-dismiss="modal"
          aria-label="Close"
        >
          <span aria-hidden="true" onClick={this.props.sertifikasitatus}>
            ×
          </span>
        </button>
      </div>
      <div class="row">
        <div class="col-sm-4">
        <div id="list-example" class="list-group">
        <a class="list-group-item list-group-item-action" href="#list-item-1">Profil</a>
        <a class="list-group-item list-group-item-action" href="#list-item-2">Biodata</a>
        <a class="list-group-item list-group-item-action" href="#list-item-3">Pengalaman Kerja</a>
        <a class="list-group-item list-group-item-action" href="#list-item-4">Pendidikan</a>
        <a class="list-group-item list-group-item-action" href="#list-item-1">Pelatihan</a>
        <a class="list-group-item list-group-item-action" href="#list-item-2">Sertifikasi</a>
        <a class="list-group-item list-group-item-action" href="#list-item-3">Sumber Lowongan Kerja</a>
        <a class="list-group-item list-group-item-action" href="#list-item-4">Organisasi</a>
        <a class="list-group-item list-group-item-action" href="#list-item-1">Keluarga</a>
        <a class="list-group-item list-group-item-action" href="#keahlian">Keahlian</a>
        <a class="list-group-item list-group-item-action" href="#list-item-3">Lain-lain</a>
        </div>
        </div>
        <div class="col-sm-8">
          <div class ="container mt-4 mr-auto">
          <h3>sertifikasi</h3>
          <button
          type="button"
          class="btn"
          onClick={this.createsertifikasiHandler}>
          <i class="fas fa-plus-circle"></i>
          </button>
      </div>
          
        <ModalBody>
        <MDBTable responsive>
                <MDBTableHead columns={data.columns}/>
                <MDBTableBody rows={data.rows}/>
              </MDBTable>
              <Viewsertifikasi
            list_sertifikasi={this.state.current_sertifikasi}
            view={this.state.viewsertifikasiHandler}
            sertifikasitatus={this.sertifikasistatus}
          />
          <Editsertifikasi
            edit={this.state.editKeahlian}
            sertifikasitatus={this.sertifikasitatus}
            list_sertifikasi={this.state.current_sertifikasi}
          />
          <Deletsertifikasi
            delete={this.state.deletsertifikasi}
            modalStatus={this.sertifikasistatus}
            list_sertifikasi={this.state.current_sertifikasi}
          />
          <Createsertifikasi
            create={this.state.createsertifikasi}
            sertifikasitatus={this.sertifikasitatus}
          />
          {/* <MDBDataTable striped bordered hover data = {data}/> */}
        </ModalBody>
      
        </div>
      </div>
      </Modal>
      
    );
  }

}
export default DatatableSertifikasi;
