import React from "react";
import { Modal,ModalHeader,ModalBody,ModalFooter, Button} from "reactstrap";
import { MDBTable, MDBTableBody,MDBTableHead} from "mdbreact";
// import Editsertifikasi from "../components/content/m_sertifikasi/edit_sertifikasi"
// import Createsertifikasi from "../components/content/m_sertifikasi/create_sertifikasi"
// import Deletesertifikasi from "../components/content/m_sertifikasi/delete_sertifikasi"
import apiconfig from '../../../config/api.config.json'
import axios from "axios";

class Bri extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      // barang: [],
      // po:[],
      // list_po:[],
      // current_po:{},
              po_detail: [],
              list_po_detail: [],
              current_po_detail: {},
              // formdata : {
              //   kt_trans:"",
              //   tgl_trans:"",
              //   kd_sub:"",
              //   userid:"",
              //   total_item:"",
              //   total_harga:"",
              //   discount:""
                
              // }
    };
  
    //this.listModalbri = this.listModalbri.bind(this);
    this.mStatus = this.mStatus.bind(this);
    // this.editsertifikasi = this.editsertifikasi.bind(this)
    // this.deletesertifikasi = this.deletesertifikasi.bind(this)
    this.po_detailstatus = this.po_detailstatus.bind(this)
    this.postatus=this.postatus.bind(this)
    this.createsertifikasi = this.createsertifikasi.bind(this)
    this.Viewsertifikasi = this.Viewsertifikasi.bind(this)
    this.Viewpo = this.Viewpo.bind(this)
  }

  // listModalbri() {
  //   this.setState({
  //     list_sertifikasi: true
  //   });
  // }
  postatus(){
    this.setState({
      viewpo:false
    })
    this.getpo()
  }
  po_detailstatus(){
    this.setState({
      Viewsertifikasi: false,
      // editsertifikasi: false,
      // deletesertifikasi: false,
      // createsertifikasi: false
    })
    this.getpo_detail()
  }
  createsertifikasi(){
    this.setState({
      createsertifikasi:true
    })
  }
  mStatus() {
    this.setState({
      list_po_detail: false,
      list_po: false
    });
  }
  // editsertifikasi(id){
  //   let tmp = {}
  //   this.state.po_detail.map((ele) =>{
  //     if(id == ele.id){
  //      tmp = ele
  //     }
  //   })
  //     this.setState({
  //       current_sertifikasi : tmp,
  //       editsertifikasi : true
  //   })
  // }
  Viewsertifikasi(id) {
    let tmp = {};

    this.state.po_detail.map(ele => {
      if (id == ele.id) {
        tmp = ele;
      }
    });

    this.setState({
      current_sertifikasi: tmp,
      viewsertifikasi: true
    });
  }
  Viewpo(kt_trans) {
    let tmp = {};

    this.state.po.map(ele => {
      if (kt_trans == ele.kt_trans) {
        tmp = ele;
      }
    });

    this.setState({
      current_po: tmp,
      viewpo: true
    });
  }

  getbarang(){
    let token = localStorage.getItem(apiconfig.ls.token);
    let option = {
       url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.barang,
      method: "get",
      headers : {
        Authorization : token
      }
    };
    axios(option)
    .then(response => {
      this.setState({
      //  list_po_detail:tmp,
        barang: response.data.message
    });
  })
    .catch(error =>{
     alert(error)
    })
  }

  getpo_detail(){
    let token = localStorage.getItem(apiconfig.ls.token);
    let option = {
       url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.po_detail,
      method: "get",
      headers : {
        Authorization : token
      }
    };
    axios(option)
    .then(response => {
      console.log(response)
      this.setState({
      //  list_po_detail:tmp,
        po_detail: response.data.message
    });
  })
    .catch(error =>{
     alert(error)
    })
  }

  getpo(){
    let token = localStorage.getItem(apiconfig.ls.token);
    let option = {
       url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.po,
      method: "get",
      headers : {
        Authorization : token
      }
    };
    axios(option)
    .then(response => {
      this.setState({
       //list_po:tmp,
        po: response.data.message
    });
  })
    .catch(error =>{
     alert(error)
    })
  }

  // deletesertifikasi(id) {
  //   this.state.po_detail.map((ele)=>{
  //       if(id == ele.id){
  //          this.setState({
  //            current_sertifikasi : ele,
  //            deletesertifikasi : true
  //          })
  //       }
  //   })
  // }
  
  componentDidMount() {
    this.getpo_detail();
  }
  
  render() {
    
    return(
      <Modal isOpen = {this.props.edit} className={this.props.className} className = "modal-dialog modal-lg"> 
      <ModalHeader className = "modal-title text-white" style = {{backgroundColor:'#00008B'}}>Edit Sertifikasi</ModalHeader>
      <ModalBody>
              {/* <form role="form"> */}
             
              <div class="row">
              <div class="col-lg-6">
         
            <label>Certificate Name</label>
            <input
              type="text"
              class="form-control"
              name="certificate_name"
              value={this.state.formdata.certificate_name}
              onChange={this.changeHandler}
              required
            />
            </div>
        
            <div class="col-lg-6">
            <label>Penerbit</label>
            <input
              type="text"
              class="form-control"
              name="publisher"
              onChange={this.changeHandler}
              value={this.state.formdata.publisher}
              required
            />
          </div>
          </div>
          <div class= "row">
          <div class="col">
          <p>Mulai Dari</p>
          </div>
          <div class = "col">
            <p>Sampai Dengan</p>
            
            </div>
            </div>
         
         <div class = "row">
         <div class="col">
      
      <Select
        name="valid_start_year"
         //value={this.state.selectedOption.valid_start_year}
         value={options1.filter(({value}) => value == this.props.list_sertifikasi.valid_start_year)}
        onChange={this.handleChange1}
        options = {options1}
        required
      />
      </div>
      <div class ="col">
      
      <Select
        name="valid_start_month"
        //value={this.state.selectedOption2.valid_start_month}
        value={month_options1.filter(({value}) => value === this.props.list_sertifikasi.valid_start_month)}
        onChange={this.handleChange2}
        options = {month_options1}
        required
      />
      </div>
    
    <div class="col">
      
      <Select
        name="until_year"
        //value={this.state.selectedOption2.until_year}
        value={options3.filter(({value}) => value == this.props.list_sertifikasi.until_year)}
        onChange={this.handleChange3}
        options = {options3}
        required
      />
      </div>
      
      <div class="col">
      
      <Select
        name="until_month"
        //value={this.state.selectedOption4.until_month}
        value={month_options2.filter(({value}) => value == this.props.list_sertifikasi.until_month)}
        onChange={this.handleChange4}
        options = {month_options2}
        required
      />
    </div>
    </div>
  
         <div class="form-group">
            <label>Notes</label>
            <input
              type="text"
              class="form-control"
              name="notes"
              value={this.state.formdata.notes}
              onChange={this.changeHandler}
              required
            />
          </div>
              <ModalFooter>
                  <Button style = {{backgroundColor:'#FFA500'}} onClick = {this.props.sertifikasistatus}>Close</Button>
                  <Button style = {{backgroundColor:'#00008B'}}  onClick = {this.submitHandler} >Save</Button>
              </ModalFooter>
          </ModalBody>
      </Modal>

  )
  }
}

export default Bri;
