import React from "react";
import { Modal,ModalHeader,ModalBody,ModalFooter, Button } from "reactstrap";
import { MDBTable, MDBTableBody,MDBTableHead} from "mdbreact";
// import Editsertifikasi from "../components/content/m_sertifikasi/edit_sertifikasi"
// import Createsertifikasi from "../components/content/m_sertifikasi/create_sertifikasi"
// import Deletesertifikasi from "../components/content/m_sertifikasi/delete_sertifikasi"
import apiconfig from '../../../config/api.config.json'
import axios from "axios";

class Bri extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      po :[],
              po_detail: [],
              list_po_detail: [],
              current_po_detail: {},
              // formdata : {
              //   kt_trans:"",
              //   tgl_trans:"",
              //   kd_sub:"",
              //   userid:"",
              //   total_item:"",
              //   total_harga:"",
              //   discount:""  
              // }
    };
  
    //this.listModalbri = this.listModalbri.bind(this);
    this.mStatus = this.mStatus.bind(this);
    // this.editsertifikasi = this.editsertifikasi.bind(this)
    // this.deletesertifikasi = this.deletesertifikasi.bind(this)
    this.po_detailstatus = this.po_detailstatus.bind(this)
    this.postatus=this.postatus.bind(this)
    this.createsertifikasi = this.createsertifikasi.bind(this)
    this.Viewsertifikasi = this.Viewsertifikasi.bind(this)
    this.Viewpo = this.Viewpo.bind(this)
  }

  // listModalbri() {
  //   this.setState({
  //     list_sertifikasi: true
  //   });
  // }
  postatus(){
    this.setState({
      viewpo:false
    })
    this.getpo()
  }
  po_detailstatus(){
    this.setState({
      Viewsertifikasi: false,
      // editsertifikasi: false,
      // deletesertifikasi: false,
      // createsertifikasi: false
    })
    this.getpo_detail()
  }
  createsertifikasi(){
    this.setState({
      createsertifikasi:true
    })
  }
  mStatus() {
    this.setState({
      list_po_detail: false,
      list_po: false
    });
  }
  // editsertifikasi(id){
  //   let tmp = {}
  //   this.state.po_detail.map((ele) =>{
  //     if(id == ele.id){
  //      tmp = ele
  //     }
  //   })
  //     this.setState({
  //       current_sertifikasi : tmp,
  //       editsertifikasi : true
  //   })
  // }
  Viewsertifikasi(id) {
    let tmp = {};

    this.state.po_detail.map(ele => {
      if (id == ele.id) {
        tmp = ele;
      }
    });

    this.setState({
      current_sertifikasi: tmp,
      viewsertifikasi: true
    });
  }
  Viewpo(kt_trans) {
    let tmp = {};

    this.state.po.map(ele => {
      if (kt_trans == ele.kt_trans) {
        tmp = ele;
      }
    });

    this.setState({
      current_po: tmp,
      viewpo: true
    });
  }

  getbarang(){
    let token = localStorage.getItem(apiconfig.ls.token);
    let option = {
       url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.barang,
      method: "get",
      headers : {
        Authorization : token
      }
    };
    axios(option)
    .then(response => {
      this.setState({
      //  list_po_detail:tmp,
        barang: response.data.message
    });
  })
    .catch(error =>{
     alert(error)
    })
  }

  getpo_detail(){
    let token = localStorage.getItem(apiconfig.ls.token);
    let option = {
       url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.po_detail,
      method: "get",
      headers : {
        Authorization : token
      }
    };
    axios(option)
    .then(response => {
      console.log(response)
      this.setState({
      //  list_po_detail:tmp,
        po_detail: response.data.message
    });
  })
    .catch(error =>{
     alert(error)
    })
  }

  getpo(){
    let token = localStorage.getItem(apiconfig.ls.token);
    let option = {
       url:apiconfig.BASE_URL+apiconfig.ENDPOINTS.po,
      method: "get",
      headers : {
        Authorization : token
      }
    };
    axios(option)
    .then(response => {
      this.setState({
       //list_po:tmp,
        po: response.data.message
    });
  })
    .catch(error =>{
     alert(error)
    })
  }

  // deletesertifikasi(id) {
  //   this.state.po_detail.map((ele)=>{
  //       if(id == ele.id){
  //          this.setState({
  //            current_sertifikasi : ele,
  //            deletesertifikasi : true
  //          })
  //       }
  //   })
  // }
  
  componentDidMount() {
    this.getpo_detail();
  }
  
  render() {
    
    return (
      <Modal isOpen={this.props.view} className={this.props.className}>
      <ModalHeader> View Unit </ModalHeader> 
      <ModalBody>

      <form class="form-inline">
              <div class ="input-group mb-3 input-group-sm">
                  <label for="text"> id : </label>
                  <input type="text" class="form-control" readOnly
                  name="id" 
                  value={this.state.po_detail.kd_trans}
                   />
                  <label for="text"> username : </label>
                  <input type="text" class="form-control" placeholder="Type Unit Name" readOnly
                  name="username" 
                  value={this.state.po_detail.tgl_trans}
                   />
              </div>
          
              <div class ="input-group mb-3 input-group-sm"> 
              <label for="text"> m_password : </label>
                  <input type="text" class="form-control" placeholder="email" readOnly
                  name="m_password" 
                  value={this.props.listm_user.m_password} 
                  />
              </div>
          </form>

      </ModalBody>

      <ModalFooter>
          <Button color="danger" onClick= {this.props.idstatus}> Close </Button>
      </ModalFooter>
  </Modal>
    );
  }
}

export default Bri;
